----------------------------------------- string = 'WENO upwind-biased interpolator' // nl string = string // '  Based on the scheme proposed by Jiang and Shu \"Efficient Implementation of Weighted ENO Schemes\", see ' // & 'JCP, 1996, vol. 126, pp. 202--228, doi:10.1006/jcph.1996.0130' // nl write ( dummy , \"(I15)\" ) 2 * self % S - 1 string = string // '  Provide a formal order of accuracy equals to: ' // trim ( adjustl ( dummy )) // nl write ( dummy , \"(I15)\" ) self % S string = string // '  Use ' // trim ( adjustl ( dummy )) // ' stencils composed by ' // trim ( adjustl ( dummy )) // ' values' // nl write ( dummy , \"(E23.15)\" ) self % eps string = string // '  The eps value used for avoiding division by zero is ' // trim ( adjustl ( dummy )) // nl string = string // '  The \"interpolate\" method has the following public API' // nl string = string // '    interpolate(S, stencil, location, interpolation)' // nl string = string // '  where:' // nl string = string // '    S: integer(I_P), intent(IN), the number of stencils actually used' // nl string = string // '    stencil(1:, 1-S:-1+S): real(R_P), intent(IN), the stencils used' // nl string = string // '    location: character(*), intent(IN), the location of interpolation {left, right, both}' // nl string = string // '    interpolation(1:, 1-S:-1+S): realR_P, intent(OUT), the interpolated values' return !--------------------------------------------------------------------------------------------------------------------------------- endsubroutine description pure subroutine interpolate ( self , S , stencil , location , interpolation ) !--------------------------------------------------------------------------------------------------------------------------------- !< Interpolate the stecil input values computing the actual interpolation. !--------------------------------------------------------------------------------------------------------------------------------- class ( weno_interpolator_upwind ), intent ( IN ) :: self !< WENO interpolator. integer , intent ( IN ) :: S !< Number of stencils actually used. real ( R_P ), intent ( IN ) :: stencil ( 1 :, 1 - S :) !< Stencil used for the interpolation, [1:2, 1-S:-1+S]. character ( * ), intent ( IN ) :: location !< Location of interpolated value(s): left, right, both. real ( R_P ), intent ( OUT ) :: interpolation ( 1 :) !< Result of the interpolation, [1:2]. real ( R_P ) :: polynomials ( 1 : 2 , 0 : S - 1 ) !< Polynomial reconstructions. real ( R_P ) :: weights ( 1 : 2 , 0 : S - 1 ) !< Weights of the stencils. !--------------------------------------------------------------------------------------------------------------------------------- !--------------------------------------------------------------------------------------------------------------------------------- select case ( location ) case ( 'both' , 'b' ) call compute_polynomials ( f1 = 1_I_P , f2 = 2_I_P , ff = 0_I_P , polynomials = polynomials ) call compute_weights ( f1 = 1_I_P , f2 = 2_I_P , ff = 0_I_P , weights = weights ) call compute_convolution ( f1 = 1_I_P , f2 = 2_I_P , ff = 0_I_P , interpolation = interpolation ) case ( 'left' , 'l' ) call compute_polynomials ( f1 = 1_I_P , f2 = 1_I_P , ff = 0_I_P , polynomials = polynomials ) call compute_weights ( f1 = 1_I_P , f2 = 1_I_P , ff = 0_I_P , weights = weights ) call compute_convolution ( f1 = 1_I_P , f2 = 1_I_P , ff = 0_I_P , interpolation = interpolation ) case ( 'right' , 'r' ) call compute_polynomials ( f1 = 2_I_P , f2 = 2_I_P , ff =- 1_I_P , polynomials = polynomials ) call compute_weights ( f1 = 2_I_P , f2 = 2_I_P , ff =- 1_I_P , weights = weights ) call compute_convolution ( f1 = 2_I_P , f2 = 2_I_P , ff =- 1_I_P , interpolation = interpolation ) endselect return !--------------------------------------------------------------------------------------------------------------------------------- contains pure subroutine compute_polynomials ( f1 , f2 , ff , polynomials ) !------------------------------------------------------------------------------------------------------------------------------- !< Compute the polynomials reconstructions. !------------------------------------------------------------------------------------------------------------------------------- integer ( I_P ), intent ( IN ) :: f1 , f2 , ff !< Faces to be computed. real ( R_P ), intent ( OUT ) :: polynomials ( 1 :, 0 :) !< Polynomial reconstructions. integer ( I_P ) :: s1 , s2 , f !< Counters. !------------------------------------------------------------------------------------------------------------------------------- !------------------------------------------------------------------------------------------------------------------------------- polynomials = 0. do s1 = 0 , S - 1 ! stencils loop do s2 = 0 , S - 1 ! values loop do f = f1 , f2 ! 1 => left interface (i-1/2), 2 => right interface (i+1/2) polynomials ( f , s1 ) = polynomials ( f , s1 ) + self % poly_coef ( f , s2 , s1 ) * stencil ( f + ff , - s2 + s1 ) enddo enddo enddo return !------------------------------------------------------------------------------------------------------------------------------- endsubroutine compute_polynomials pure subroutine compute_weights ( f1 , f2 , ff , weights ) !------------------------------------------------------------------------------------------------------------------------------- !< Compute the stencils weights. !------------------------------------------------------------------------------------------------------------------------------- integer , intent ( IN ) :: f1 , f2 , ff !< Faces to be computed. real ( R_P ), intent ( OUT ) :: weights ( 1 :, 0 :) !< Weights of the stencils, [1:2, 0:S - 1 ]. real ( R_P ) :: IS ( 1 : 2 , 0 : S - 1 ) !< Smoothness indicators of the stencils. real ( R_P ) :: a ( 1 : 2 , 0 : S - 1 ) !< Alpha coefficients for the weights. real ( R_P ) :: a_tot ( 1 : 2 ) !< Sum of the alpha coefficients. integer ( I_P ) :: s1 , s2 , s3 , f !< Counters. !------------------------------------------------------------------------------------------------------------------------------- !------------------------------------------------------------------------------------------------------------------------------- ! computing smoothness indicators do s1 = 0 , S - 1 ! stencils loop do f = f1 , f2 ! 1 => left interface (i-1/2), 2 => right interface (i+1/2) IS ( f , s1 ) = 0. do s2 = 0 , S - 1 do s3 = 0 , S - 1 IS ( f , s1 ) = IS ( f , s1 ) + self % smooth_coef ( s3 , s2 , s1 ) * stencil ( f + ff , s1 - s3 ) * stencil ( f + ff , s1 - s2 ) enddo enddo enddo enddo ! computing alfa coefficients a_tot = 0. do s1 = 0 , S - 1 ! stencil loops do f = f1 , f2 ! 1 => left interface (i-1/2), 2 => right interface (i+1/2) a ( f , s1 ) = self % weights_opt ( f , s1 ) * ( 1. / ( self % eps + IS ( f , s1 )) ** S ) ; a_tot ( f ) = a_tot ( f ) + a ( f , s1 ) enddo enddo ! computing the weights do s1 = 0 , S - 1 ! stencils loop do f = f1 , f2 ! 1 => left interface (i-1/2), 2 => right interface (i+1/2) weights ( f , s1 ) = a ( f , s1 ) / a_tot ( f ) enddo enddo return !------------------------------------------------------------------------------------------------------------------------------- endsubroutine compute_weights pure subroutine compute_convolution ( f1 , f2 , ff , interpolation ) !------------------------------------------------------------------------------------------------------------------------------- !< Compute the polynomials convolution. !------------------------------------------------------------------------------------------------------------------------------- integer ( I_P ), intent ( IN ) :: f1 , f2 , ff !< Faces to be computed. real ( R_P ), intent ( OUT ) :: interpolation ( 1 :) !< Left and right (1,2) interface value of reconstructed. integer ( I_P ) :: k , f !< Counters. !------------------------------------------------------------------------------------------------------------------------------- !------------------------------------------------------------------------------------------------------------------------------- ! computing the convultion interpolation = 0. do k = 0 , S - 1 ! stencils loop do f = f1 , f2 ! 1 => left interface (i-1/2), 2 => right interface (i+1/2) interpolation ( f + ff ) = interpolation ( f + ff ) + weights ( f , k ) * polynomials ( f , k ) enddo enddo return !------------------------------------------------------------------------------------------------------------------------------- endsubroutine compute_convolution endsubroutine interpolate ! private methods pure subroutine assign_interpolator ( lhs , rhs ) !--------------------------------------------------------------------------------------------------------------------------------- !< Assign one interpolator to another. !--------------------------------------------------------------------------------------------------------------------------------- class ( weno_interpolator_upwind ), intent ( INOUT ) :: lhs !< Left hand side. class ( weno_interpolator ), intent ( IN ) :: rhs !< Right hand side. !--------------------------------------------------------------------------------------------------------------------------------- !--------------------------------------------------------------------------------------------------------------------------------- select type ( rhs ) class is ( weno_interpolator_upwind ) lhs % S = rhs % S lhs % eps = rhs % eps if ( allocated ( rhs % weights_opt )) lhs % weights_opt = rhs % weights_opt if ( allocated ( rhs % poly_coef )) lhs % poly_coef = rhs % poly_coef if ( allocated ( rhs % smooth_coef )) lhs % smooth_coef = rhs % smooth_coef endselect return !--------------------------------------------------------------------------------------------------------------------------------- endsubroutine assign_interpolator elemental subroutine finalize ( self ) !--------------------------------------------------------------------------------------------------------------------------------- !< Finalize object. !--------------------------------------------------------------------------------------------------------------------------------- type ( weno_interpolator_upwind ), intent ( INOUT ) :: self !< WENO interpolator. !--------------------------------------------------------------------------------------------------------------------------------- !--------------------------------------------------------------------------------------------------------------------------------- call self % destroy return !--------------------------------------------------------------------------------------------------------------------------------- endsubroutine finalize endmodule type_weno_interpolator_upwind","tags":"","loc":"sourcefile/type_weno_interpolator_upwind.f90.html","title":"type_weno_interpolator_upwind.f90 â€“ Shu-Osher-shock-tube-problem"},{"text":"Source Code !***************************************************************************************** !> author: Jacob Williams !  date: 4/14/2015 !  license: BSD ! !  For making simple x-y plots from Fortran. !  It works by generating a Python script and executing it. ! !# See also !   * Inspired by: [EasyPlot](https://pypi.python.org/pypi/EasyPlot) module pyplot_module use , intrinsic :: iso_fortran_env , only : real64 implicit none private integer , parameter , private :: wp = real64 !! Default real kind [8 bytes]. character ( len =* ), parameter :: tmp_file = 'pyplot_module_temp_1234567890.py' !! Default name of the temporary file !! (this can also be user-specified). character ( len =* ), parameter :: python_exe = 'python' !! The python executable name. character ( len =* ), parameter :: int_fmt = '(I10)' !! integer format string integer , parameter :: max_int_len = 10 !! max string length for integers character ( len =* ), parameter :: real_fmt = '(E30.16)' !! real number format string integer , parameter :: max_real_len = 30 !! max string length for reals type , public :: pyplot !!  The main pyplot class. private character ( len = :), allocatable :: str !! string buffer logical :: show_legend = . false . !! show legend into plot logical :: use_numpy = . true . !! use numpy python module logical :: mplot3d = . false . !! it is a 3d plot contains ! public methods procedure , public :: initialize !! initialize pyplot instance procedure , public :: add_plot !! add a 2d plot to pyplot instance procedure , public :: add_3d_plot !! add a 3d plot to pyplot instance procedure , public :: add_bar !! add a barplot to pyplot instance procedure , public :: savefig !! save plots of pyplot instance procedure , public :: destroy !! destroy pyplot instance ! private methods procedure :: execute !! execute pyplot commands procedure :: add_str !! add string to pytplot instance buffer end type pyplot contains !***************************************************************************************** !***************************************************************************************** !> author: Jacob Williams ! ! Destructor. subroutine destroy ( me ) class ( pyplot ), intent ( inout ) :: me !! pyplot handler if ( allocated ( me % str )) deallocate ( me % str ) end subroutine destroy !***************************************************************************************** !***************************************************************************************** !> author: Jacob Williams ! ! Add a string to the buffer. subroutine add_str ( me , str ) class ( pyplot ), intent ( inout ) :: me !! pyplot handler character ( len =* ), intent ( in ) :: str !! str to be added to pyplot handler buffer me % str = me % str // str // new_line ( ' ' ) end subroutine add_str !***************************************************************************************** !***************************************************************************************** !> author: Jacob Williams ! ! Initialize a plot subroutine initialize ( me , grid , xlabel , ylabel , zlabel , title , legend , use_numpy , figsize , & font_size , axes_labelsize , xtick_labelsize , ytick_labelsize , ztick_labelsize , & legend_fontsize , mplot3d ) class ( pyplot ), intent ( inout ) :: me !! pyplot handler logical , intent ( in ), optional :: grid !! activate grid drawing character ( len =* ), intent ( in ), optional :: xlabel !! label of x axis character ( len =* ), intent ( in ), optional :: ylabel !! label of y axis character ( len =* ), intent ( in ), optional :: zlabel !! label of z axis character ( len =* ), intent ( in ), optional :: title !! plot title logical , intent ( in ), optional :: legend !! plot legend logical , intent ( in ), optional :: use_numpy !! activate usage of numpy python module integer , dimension ( 2 ), intent ( in ), optional :: figsize !! dimension of the figure integer , intent ( in ), optional :: font_size !! font size integer , intent ( in ), optional :: axes_labelsize !! size of axis labels integer , intent ( in ), optional :: xtick_labelsize !! size of x axis tick lables integer , intent ( in ), optional :: ytick_labelsize !! size of y axis tick lables integer , intent ( in ), optional :: ztick_labelsize !! size of z axis tick lables integer , intent ( in ), optional :: legend_fontsize !! size of legend font logical , intent ( in ), optional :: mplot3d !! set true for 3d plots character ( len = max_int_len ) :: width_str !! figure width dummy string character ( len = max_int_len ) :: height_str !! figure height dummy string character ( len = max_int_len ) :: font_size_str !! font size dummy string character ( len = max_int_len ) :: axes_labelsize_str !! size of axis labels dummy string character ( len = max_int_len ) :: xtick_labelsize_str !! size of x axis tick labels dummy string character ( len = max_int_len ) :: ytick_labelsize_str !! size of x axis tick labels dummy string character ( len = max_int_len ) :: ztick_labelsize_str !! size of z axis tick labels dummy string character ( len = max_int_len ) :: legend_fontsize_str !! size of legend font dummy string character ( len =* ), parameter :: default_font_size_str = '10' !! the default font size for plots call me % destroy () if ( present ( legend )) then me % show_legend = legend else me % show_legend = . false . end if if ( present ( use_numpy )) then me % use_numpy = use_numpy else me % use_numpy = . true . end if if ( present ( figsize )) then call integer_to_string ( figsize ( 1 ), width_str ) call integer_to_string ( figsize ( 2 ), height_str ) end if if ( present ( mplot3d )) then me % mplot3d = mplot3d else me % mplot3d = . false . end if call optional_int_to_string ( font_size , font_size_str , default_font_size_str ) call optional_int_to_string ( axes_labelsize , axes_labelsize_str , default_font_size_str ) call optional_int_to_string ( xtick_labelsize , xtick_labelsize_str , default_font_size_str ) call optional_int_to_string ( ytick_labelsize , ytick_labelsize_str , default_font_size_str ) call optional_int_to_string ( ztick_labelsize , ztick_labelsize_str , default_font_size_str ) call optional_int_to_string ( legend_fontsize , legend_fontsize_str , default_font_size_str ) me % str = '' call me % add_str ( '#!/usr/bin/python' ) call me % add_str ( '' ) call me % add_str ( 'import matplotlib' ) call me % add_str ( 'import matplotlib.pyplot as plt' ) if ( me % mplot3d ) call me % add_str ( 'from mpl_toolkits.mplot3d import Axes3D' ) if ( me % use_numpy ) call me % add_str ( 'import numpy as np' ) call me % add_str ( '' ) call me % add_str ( 'matplotlib.rcParams[\"font.family\"] = \"Serif\"' ) call me % add_str ( 'matplotlib.rcParams[\"font.size\"] = ' // trim ( font_size_str )) call me % add_str ( 'matplotlib.rcParams[\"axes.labelsize\"] = ' // trim ( axes_labelsize_str )) call me % add_str ( 'matplotlib.rcParams[\"xtick.labelsize\"] = ' // trim ( xtick_labelsize_str )) call me % add_str ( 'matplotlib.rcParams[\"ytick.labelsize\"] = ' // trim ( ytick_labelsize_str )) call me % add_str ( 'matplotlib.rcParams[\"legend.fontsize\"] = ' // trim ( legend_fontsize_str )) call me % add_str ( '' ) if ( present ( figsize )) then !if specifying the figure size call me % add_str ( 'fig = plt.figure(figsize=(' // trim ( width_str ) // ',' // trim ( height_str ) // '))' ) else call me % add_str ( 'fig = plt.figure()' ) end if if ( me % mplot3d ) then call me % add_str ( 'ax = fig.gca(projection=''3d'')' ) else call me % add_str ( 'ax = fig.gca()' ) end if if ( present ( grid )) then if ( grid ) call me % add_str ( 'ax.grid()' ) end if if ( present ( xlabel )) call me % add_str ( 'ax.set_xlabel(\"' // trim ( xlabel ) // '\")' ) if ( present ( ylabel )) call me % add_str ( 'ax.set_ylabel(\"' // trim ( ylabel ) // '\")' ) if ( present ( zlabel )) call me % add_str ( 'ax.set_zlabel(\"' // trim ( zlabel ) // '\")' ) if ( present ( title )) call me % add_str ( 'ax.set_title(\"' // trim ( title ) // '\")' ) call me % add_str ( '' ) end subroutine initialize !***************************************************************************************** !***************************************************************************************** !> author: Jacob Williams ! ! Add an x,y plot. subroutine add_plot ( me , x , y , label , linestyle , markersize , linewidth ) class ( pyplot ), intent ( inout ) :: me !! pyplot handler real ( wp ), dimension (:), intent ( in ) :: x !! x values real ( wp ), dimension (:), intent ( in ) :: y !! y values character ( len =* ), intent ( in ) :: label !! plot label character ( len =* ), intent ( in ) :: linestyle !! style of the plot line integer , intent ( in ), optional :: markersize !! size of the plot markers integer , intent ( in ), optional :: linewidth !! width of the plot line character ( len = :), allocatable :: xstr !! x values strinfied character ( len = :), allocatable :: ystr !! y values strinfied character ( len = max_int_len ) :: imark !! actual markers size character ( len = max_int_len ) :: iline !! actual line width character ( len =* ), parameter :: xname = 'x' !! x variable name for script character ( len =* ), parameter :: yname = 'y' !! y variable name for script if ( allocated ( me % str )) then !convert the arrays to strings: call vec_to_string ( x , xstr , me % use_numpy ) call vec_to_string ( y , ystr , me % use_numpy ) !get optional inputs (if not present, set default value): call optional_int_to_string ( markersize , imark , '3' ) call optional_int_to_string ( linewidth , iline , '3' ) !write the arrays: call me % add_str ( trim ( xname ) // ' = ' // xstr ) call me % add_str ( trim ( yname ) // ' = ' // ystr ) call me % add_str ( '' ) !write the plot statement: call me % add_str ( 'ax.plot(' // & trim ( xname ) // ',' // & trim ( yname ) // ',' // & '\"' // trim ( linestyle ) // '\",' // & 'linewidth=' // trim ( adjustl ( iline )) // ',' // & 'markersize=' // trim ( adjustl ( imark )) // ',' // & 'label=\"' // trim ( label ) // '\")' ) call me % add_str ( '' ) else error stop 'Error in add_plot: pyplot class not properly initialized.' end if end subroutine add_plot !***************************************************************************************** !***************************************************************************************** !> author: Jacob Williams ! ! Add a 3D x,y,z plot. ! !@note Must initialize the class with ```mplot3d=.true.``` subroutine add_3d_plot ( me , x , y , z , label , linestyle , markersize , linewidth ) class ( pyplot ), intent ( inout ) :: me !! pyplot handler real ( wp ), dimension (:), intent ( in ) :: x !! x values real ( wp ), dimension (:), intent ( in ) :: y !! y values real ( wp ), dimension (:), intent ( in ) :: z !! z values character ( len =* ), intent ( in ) :: label !! plot label character ( len =* ), intent ( in ) :: linestyle !! style of the plot line integer , intent ( in ), optional :: markersize !! size of the plot markers integer , intent ( in ), optional :: linewidth !! width of the plot line character ( len = :), allocatable :: xstr !! x values strinfied character ( len = :), allocatable :: ystr !! y values strinfied character ( len = :), allocatable :: zstr !! z values strinfied character ( len = max_int_len ) :: imark !! actual markers size character ( len = max_int_len ) :: iline !! actual line width character ( len =* ), parameter :: xname = 'x' !! x variable name for script character ( len =* ), parameter :: yname = 'y' !! y variable name for script character ( len =* ), parameter :: zname = 'z' !! z variable name for script if ( allocated ( me % str )) then !convert the arrays to strings: call vec_to_string ( x , xstr , me % use_numpy ) call vec_to_string ( y , ystr , me % use_numpy ) call vec_to_string ( z , zstr , me % use_numpy ) !get optional inputs (if not present, set default value): call optional_int_to_string ( markersize , imark , '3' ) call optional_int_to_string ( linewidth , iline , '3' ) !write the arrays: call me % add_str ( trim ( xname ) // ' = ' // xstr ) call me % add_str ( trim ( yname ) // ' = ' // ystr ) call me % add_str ( trim ( zname ) // ' = ' // zstr ) call me % add_str ( '' ) !write the plot statement: call me % add_str ( 'ax.plot(' // & trim ( xname ) // ',' // & trim ( yname ) // ',' // & trim ( zname ) // ',' // & '\"' // trim ( linestyle ) // '\",' // & 'linewidth=' // trim ( adjustl ( iline )) // ',' // & 'markersize=' // trim ( adjustl ( imark )) // ',' // & 'label=\"' // trim ( label ) // '\")' ) call me % add_str ( '' ) else error stop 'Error in add_3d_plot: pyplot class not properly initialized.' end if end subroutine add_3d_plot !***************************************************************************************** !***************************************************************************************** !> author: Jacob Williams ! ! Add a bar plot. subroutine add_bar ( me , left , height , label , width , bottom , color ) class ( pyplot ), intent ( inout ) :: me !! pyplot handler real ( wp ), dimension (:), intent ( in ) :: left !! left bar values real ( wp ), dimension (:), intent ( in ) :: height !! height bar values character ( len =* ), intent ( in ) :: label !! plot label real ( wp ), dimension (:), intent ( in ), optional :: width !! width values real ( wp ), dimension (:), intent ( in ), optional :: bottom !! bottom values character ( len =* ), intent ( in ), optional :: color !! plot color character ( len = :), allocatable :: xstr !! x axis values stringified character ( len = :), allocatable :: ystr !! y axis values stringified character ( len = :), allocatable :: wstr !! width values stringified character ( len = :), allocatable :: bstr !! bottom values stringified character ( len = :), allocatable :: plt_str !! plot string character ( len =* ), parameter :: xname = 'x' !! x axis name character ( len =* ), parameter :: yname = 'y' !! y axis name character ( len =* ), parameter :: wname = 'w' !! width name character ( len =* ), parameter :: bname = 'b' !! bottom name if ( allocated ( me % str )) then !convert the arrays to strings: call vec_to_string ( left , xstr , me % use_numpy ) call vec_to_string ( height , ystr , me % use_numpy ) if ( present ( width )) call vec_to_string ( width , wstr , me % use_numpy ) if ( present ( bottom )) call vec_to_string ( bottom , bstr , me % use_numpy ) !write the arrays: call me % add_str ( trim ( xname ) // ' = ' // xstr ) call me % add_str ( trim ( yname ) // ' = ' // ystr ) if ( present ( width )) call me % add_str ( trim ( wname ) // ' = ' // wstr ) if ( present ( bottom )) call me % add_str ( trim ( bname ) // ' = ' // bstr ) call me % add_str ( '' ) !create the plot string: plt_str = 'ax.bar(' // & 'left=' // trim ( xname ) // ',' // & 'height=' // trim ( yname ) // ',' if ( present ( width )) plt_str = plt_str // 'width=' // trim ( wname ) // ',' if ( present ( bottom )) plt_str = plt_str // 'bottom=' // trim ( bstr ) // ',' if ( present ( color )) plt_str = plt_str // 'color=\"' // trim ( color ) // '\",' plt_str = plt_str // 'label=\"' // trim ( label ) // '\")' !write the plot statement: call me % add_str ( plt_str ) call me % add_str ( '' ) else error stop 'Error in add_bar: pyplot class not properly initialized.' end if end subroutine add_bar !***************************************************************************************** !***************************************************************************************** !> author: Jacob Williams ! ! Integer to string, specifying the default value if ! the optional argument is not present. subroutine optional_int_to_string ( int_value , string_value , default_value ) integer , intent ( in ), optional :: int_value !! integer value character ( len =* ), intent ( out ) :: string_value !! integer value stringified character ( len =* ), intent ( in ) :: default_value !! default integer value if ( present ( int_value )) then call integer_to_string ( int_value , string_value ) else string_value = default_value end if end subroutine optional_int_to_string !***************************************************************************************** !***************************************************************************************** !> author: Jacob Williams ! ! Integer to string conversion. subroutine integer_to_string ( i , s ) integer , intent ( in ), optional :: i !! integer value character ( len =* ), intent ( out ) :: s !! integer value stringified integer :: istat !! IO status write ( s , int_fmt , iostat = istat ) i if ( istat /= 0 ) then error stop 'Error converting integer to string' else s = adjustl ( s ) end if end subroutine integer_to_string !***************************************************************************************** !***************************************************************************************** !> author: Jacob Williams ! ! Real vector to string. subroutine vec_to_string ( v , str , use_numpy ) real ( wp ), dimension (:), intent ( in ) :: v !! real values character ( len = :), allocatable , intent ( out ) :: str !! real values stringified logical , intent ( in ) :: use_numpy !! activate numpy python module usage integer :: i !! counter integer :: istat !! IO status character ( len = max_real_len ) :: tmp !! dummy string str = '[' do i = 1 , size ( v ) write ( tmp , real_fmt , iostat = istat ) v ( i ) if ( istat /= 0 ) error stop 'Error in vec_to_string' str = str // trim ( adjustl ( tmp )) if ( i < size ( v )) str = str // ',' end do str = str // ']' !convert to numpy array if necessary: if ( use_numpy ) str = 'np.array(' // str // ')' end subroutine vec_to_string !***************************************************************************************** !***************************************************************************************** !> author: Jacob Williams !  date: 8/16/2015 ! !  Write the buffer to a file, and then execute it with Python. ! !  If user specifies a Python file name, then the file is kept, otherwise !  a temporary filename is used, and the file is deleted after it is used. subroutine execute ( me , pyfile ) class ( pyplot ), intent ( inout ) :: me !! pytplot handler character ( len =* ), intent ( in ), optional :: pyfile !! name of the python script to generate integer :: istat !! IO status integer :: iunit !! IO unit character ( len = :), allocatable :: file !! file name logical :: scratch !! if a scratch file is to be used if ( allocated ( me % str )) then scratch = (. not . present ( pyfile )) !file name to use: if ( scratch ) then file = trim ( tmp_file ) !use the default else file = trim ( pyfile ) !use the user-specified name end if !open the file: open ( newunit = iunit , file = file , status = 'REPLACE' , iostat = istat ) if ( istat /= 0 ) error stop 'Error opening file.' !write to the file: write ( iunit , '(A)' ) me % str !run the file using python: call execute_command_line ( python_exe // ' ' // file ) !close the file: if ( scratch ) then close ( iunit , status = 'DELETE' , iostat = istat ) else close ( iunit , iostat = istat ) end if if ( istat /= 0 ) error stop 'Error closing file.' !cleanup: if ( allocated ( file )) deallocate ( file ) end if end subroutine execute !***************************************************************************************** !***************************************************************************************** !> author: Jacob Williams ! ! Save the figure. subroutine savefig ( me , figfile , pyfile ) class ( pyplot ), intent ( inout ) :: me !! pyplot handler character ( len =* ), intent ( in ) :: figfile !! file name for the figure character ( len =* ), intent ( in ), optional :: pyfile !! name of the Python script to generate if ( allocated ( me % str )) then !finish up the string: if ( me % show_legend ) then call me % add_str ( 'ax.legend(loc=\"best\")' ) call me % add_str ( '' ) end if call me % add_str ( 'plt.savefig(\"' // trim ( figfile ) // '\")' ) !run it: call me % execute ( pyfile ) else error stop 'error in savefig: pyplot class not properly initialized.' end if end subroutine savefig !***************************************************************************************** !***************************************************************************************** end module pyplot_module !*****************************************************************************************","tags":"","loc":"sourcefile/pyplot_module.f90.html","title":"pyplot_module.f90 â€“ Shu-Osher-shock-tube-problem"},{"text":"FLAP, Fortran command Line Arguments Parser for poor people Source Code !< FLAP, Fortran command Line Arguments Parser for poor people module Data_Type_Command_Line_Interface !----------------------------------------------------------------------------------------------------------------------------------- !< FLAP, Fortran command Line Arguments Parser for poor people !<{!README-FLAP.md!} !< !<### ChangeLog !< !<{!ChangeLog-FLAP.md!} !----------------------------------------------------------------------------------------------------------------------------------- USE IR_Precision ! Integers and reals precision definition. USE , intrinsic :: ISO_FORTRAN_ENV , only : stdout => OUTPUT_UNIT , stderr => ERROR_UNIT ! Standard output/error logical units. !----------------------------------------------------------------------------------------------------------------------------------- !----------------------------------------------------------------------------------------------------------------------------------- implicit none private save !----------------------------------------------------------------------------------------------------------------------------------- !----------------------------------------------------------------------------------------------------------------------------------- type , abstract :: Type_Object private character ( len = :), public , allocatable :: progname !< Program name. character ( len = :), public , allocatable :: version !< Program version. character ( len = :), allocatable :: help !< Help message. character ( len = :), public , allocatable :: description !< Detailed description. character ( len = :), public , allocatable :: license !< License description. character ( len = :), public , allocatable :: authors !< Authors list. character ( len = :), public , allocatable :: epilog !< Epilog message. character ( len = :), public , allocatable :: m_exclude !< Mutually exclude other CLA(s group). integer ( I4P ), public :: error = 0_I4P !< Error traping flag. contains procedure :: free_object !< Free dynamic memory. procedure :: errored !< Trig error occurence and print meaningful message. procedure :: print_version !< Print version. procedure :: assign_object !< Assignment overloading. endtype Type_Object type , extends ( Type_Object ) :: Type_Command_Line_Argument !< Command line arguments (CLA). !< !< @note If not otherwise declared the action on CLA value is set to \"store\" a value. private character ( len = :), allocatable :: switch !< Switch name. character ( len = :), allocatable :: switch_ab !< Abbreviated switch name. logical :: required = . false . !< Flag for set required argument. logical :: positional = . false . !< Flag for checking if CLA is a positional or a named CLA. integer ( I4P ) :: position = 0_I4P !< Position of positional CLA. logical :: passed = . false . !< Flag for checking if CLA has been passed to CLI. logical :: hidden = . false . !< Flag for hiding CLA, thus it does not compare into help. character ( len = :), allocatable :: act !< CLA value action. character ( len = :), allocatable :: def !< Default value. character ( len = :), allocatable :: nargs !< Number of arguments consumed by CLA. character ( len = :), allocatable :: choices !< List (comma separated) of allowable values for the argument. character ( len = :), allocatable :: val !< CLA value. character ( len = :), allocatable :: envvar !< Environment variable from which take value. contains ! public methods procedure , public :: free => free_cla !< Free dynamic memory. procedure , public :: check => check_cla !< Check CLA data consistency. procedure , public :: check_choices => check_choices_cla !< Check if CLA value is in allowed choices. procedure , public :: check_list_size => check_list_size_cla !< Check CLA multiple values list size consistency. generic , public :: get => get_cla , get_cla_list !< Get CLA value(s). generic , public :: get_varying => & !< Get CLA value(s) from CLAs list parsedi, varying size list. #ifdef r16p get_cla_list_varying_R16P , & #endif get_cla_list_varying_R8P , & get_cla_list_varying_R4P , & get_cla_list_varying_I8P , & get_cla_list_varying_I4P , & get_cla_list_varying_I2P , & get_cla_list_varying_I1P , & get_cla_list_varying_logical , & get_cla_list_varying_char procedure , public :: usage => usage_cla !< Get correct CLA usage. procedure , public :: signature => signature_cla !< Get CLA signature for adding to CLI one. ! private methods procedure , private :: get_cla !< Get CLA (single) value from CLAs list parsed. procedure , private :: get_cla_list !< Get CLA multiple values from CLAs list 